changeSet.sql:

drop table if exists companies;;
create table companies
(
    id            varchar(36),
    name          text,
    registered_at timestamp default now(),

    primary key (id)
);;


drop table if exists subdivisions;;
create table subdivisions
(
    id            varchar(36),
    name          text,
    company_id    varchar(36),
    registered_at timestamp default now(),

    primary key (id),
    constraint fk_subdivision_company FOREIGN KEY (company_id) references companies (id)
);;


drop table if exists documents;;
create table documents
(
    id            varchar(36),
    name          text,
    registered_at timestamp default now(),

    primary key (id)
);;


drop table if exists material_losses;;
create table material_losses
(
    id            varchar(36),
    document_id   varchar(36),
    expired_at    timestamp,
    registered_at timestamp default now(),

    primary key (id),
    constraint fk_material_losses_document foreign key (document_id) references documents (id)
);;
